INSERT INTO Projects (project_id,title,creator,img_src,description,start_date,end_date,goal,raised) VALUES (DEFAULT,'Engraved Gemstone Dice!','kayewagner@sitamet.com','https://s3-ap-southeast-1.amazonaws.com/cs2102jan17g11-crowdfundme/Engraved+Gemstone+Dice.png','Beautiful gemstone dice, now engraved! Available in Opalite, Blue Sandstone, Tigers Eye, Amethyst, & possibly more with stretch goals! All of our gemstone dice sets are full-sized, 16mm dice. We generally sell our gemstone dice in polyhedral sets (d4, d6, d8, d10, d10%, d12, and d20).','2016-01-16','2017-09-13',14000,10582);
INSERT INTO Projects (project_id,title,creator,img_src,description,start_date,end_date,goal,raised) VALUES (DEFAULT,'CHIPPO: The New Golf Game','kayewagner@sitamet.com','https://s3-ap-southeast-1.amazonaws.com/cs2102jan17g11-crowdfundme/CHIPPO+The+New+Golf+Game.png','hippo is our very own invention: a patent pending, mind-blowing mash-up of golf and traditional backyard games. We like to call Chippo the glorious lovechild of golf and cornhole (the popular bean bag toss game). We’ve combined the old fashioned, beer-slugging, tailgate-dominating, sunshine-basking goodness of games like cornhole with the gentlemanly, giddiness-inducing, side-betting beauty of golf. Chippo is the only backyard game where you can work over your buddies, while working on your short game. Sound good? Well, we are ready to bring Chippo to beaches, backyards, tailgates, gym classes and golf clubhouses all over these great United States of America...but we need your help!','2016-03-08','2017-12-11',12000,5640);
